ui:SelectionView.js:Script:
(function () {

    return {
        $classAttributes: ['productType','product', 'colorWidth'],

        defaults: {
            componentClass: 'product-appearances',
            itemKey: 'appearance',
            colorWidth: 25,
            // if null use default view
            productType: null,
            product: null,
            multiSelect: false
        },
        _commitChangedAttributes: function (attributes) {
            if (attributes.productType && attributes.productType.$.appearances) {
                // if the article has the same
                if (attributes.productType.$.appearances !== this.$items) {
                    var productType = attributes.productType;
                    this.set('items', productType.$.appearances);
                    this.set('selectedItem', productType.getDefaultAppearance());
                }
            } else if (attributes.productType === null) {
                // set items to null
                this.set('items', []);
            }

            if (attributes.product) {
                this.set('productType', attributes.product.$.productType);
                this.set('selectedItem', this.$.productType.getAppearanceById(attributes.product.$.appearance.id));
            }

            this.callBase();
        },
        getImageUrl: function (appearance) {
            if (appearance.resources && appearance.resources.length > 0) {
                return appearance.resources[0].href + ",width=" + this.$.colorWidth;
            }
        }
    }
})
