(function (_) {

    var exceptionSizes = [120, 178];

    return {

        defaults: {
            tagName: "img"
        },

        normalizeSize: function(size) {

            if (_.indexOf(exceptionSizes, size) !== -1) {
                // normalize size
                size = Math.ceil(size / 50) * 50;
            }

            // keep range
            return Math.max(50, Math.min(1200, size));
        },

        imageUrl: function() {
            return "";
        },

        alt: function() {
            return "";
        }

    }
})
