ui:SelectionView.js:Script:
(function () {

    return {
        $classAttributes: ['article', 'appearance'],

        defaults: {
            componentClass: 'product-views',
            tagName: 'ul',
            itemKey: 'view',
            // if null use default view
            article: null,
            $article: null,
            $appearance: null,
            appearance: null,
            needsSelection: true,
            multiSelect: false
        },
        _commitChangedAttributes: function (attributes) {
            if (attributes.article) {
                // if the article has the same
                if (attributes.article.$.product.$.productType.$.views.$items === this.$items) {
                    this.set({
                        $article: attributes.article,
                        $appearance: attributes.appearance || this.$.$appearance
                    });
                } else {
                    this.set({$article:  attributes.article, $appearance: attributes.appearance || this.$.$appearance}, {silent: true});
                    this.set('items', attributes.article.$.product.$.productType.$.views.$items);
                }
            } else if (attributes.article === null) {
                // set items to null
                this.set('items', []);
            } else if (attributes.appearance) {
                this.set({
                    $appearance: attributes.appearance
                });
            } else if(attributes.selectedItem){

            }

            this.callBase();
        }
    }
})
