ui:SelectionView.js:Script:
(function () {

    return {
        $classAttributes: ['productType'],

        defaults: {
            componentClass: 'product-sizes',
            itemKey: 'size',
            // if null use default view
            productType: null,
            multiSelect: false
        },
        _commitChangedAttributes: function (attributes) {
            if (attributes.productType) {
                // if the article has the same
                if (attributes.productType.$.views.$items !== this.$items) {
                    this.set('items', attributes.productType.$.sizes.$items);
                    if (this.$.items.length === 1) {
                        this.set('selectedItem', this.$.items[0]);
                    }
                }
            } else if (attributes.article === null) {
                // set items to null
                // this.set({needsSelection: false, selectedItem: null});
                this.set('items', []);

            }

            this.callBase();
        },
        selectSize: function (e, element) {
            this.set('selectedItem', element.find('$size'));
        }
    }
})
